@connect(
  state => ({
    wrapEnabled: state.config.get('editorWrapText'),
  })
)
class Editor extends Component {

  static aceProps = {
    $blockScrolling: true,
  }

  constructor (props) {
    super(props)

    this.state = {
      content: props.value,
    }
  }

  componentDidMount () {
    const editor = this.refs.ace.editor
    const register = (s) => editor.commands.addCommand(s)

    editor.focus()
    this.props.dispatch(registerShortcuts(register))
  }

