    const { theme, wrapEnabled } = this.props

    return (
      <div className='Editor'>
        <AceEditor
          ref='ace'
          mode='xml'
          theme={theme}
          wrapEnabled={wrapEnabled}
          height='100%'
          value={content}
          tabSize={2}
          onChange={this.handleChange}
          name='editor'
          editorProps={Editor.aceProps}/>
      </div>
    )
  }

}

